import React, { useState } from "react";
import axios from "axios";
import "./style.css"; // Aurora motion background

const API_BASE = "http://127.0.0.1:8000";

export default function App() {
  // === React State ===
  const [message, setMessage] = useState("");
  const [response, setResponse] = useState("");
  const [isTyping, setIsTyping] = useState(false);

  // === Send Message ===
  const sendMessage = async () => {
    if (!message.trim()) return;

    setIsTyping(true);
    setResponse("");

    try {
      const res = await axios.post(`${API_BASE}/api/ask/`, { message });
      const reply = (res.data?.reply ?? "Sorry, I didn’t understand that.").toString();

      // Typing animation (fixed — no doubled letters)
      let currentText = "";
      for (let i = 0; i < reply.length; i++) {
        await new Promise((resolve) => setTimeout(resolve, 20)); // speed
        currentText += reply[i];
        setResponse(currentText);
      }

      setIsTyping(false);
    } catch (error) {
      console.error(error);
      setResponse("⚠️ Error connecting to server.");
      setIsTyping(false);
    }

    setMessage("");
  };

  // === Handle Enter key ===
  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  // === UI ===
  return (
    <div className="relative min-h-screen w-full flex flex-col bg-black text-white">
      {/* Background */}
      <div id="aurora-bg" />

      {/* Chat container */}
      <div className="relative z-10 flex flex-col justify-between h-screen max-w-2xl mx-auto p-4">
        {/* Logo / Title */}
        <div className="flex justify-center items-center py-4">
          <h1 className="text-3xl font-semibold text-aurora drop-shadow-md">
            Aurora
          </h1>
        </div>

        {/* Chat area */}
        <div className="flex-1 overflow-y-auto p-3 bg-transparent backdrop-blur-sm rounded-lg">
          {response ? (
            <div className="p-3 rounded-lg bg-white/5 border border-white/10">
              <p className="whitespace-pre-line text-gray-100">{response}</p>
            </div>
          ) : (
            <p className="text-gray-500 italic text-center">
              Ask Aurora something ✨
            </p>
          )}

          {isTyping && (
            <p className="typing text-green-300 mt-2 animate-pulse">
              Aurora is typing...
            </p>
          )}
        </div>

        {/* Input area */}
        <div className="flex mt-4">
          <textarea
            className="flex-1 resize-none p-3 rounded-lg bg-gray-900/70 text-gray-200 focus:outline-none border border-white/10"
            rows="2"
            placeholder="Type your message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={handleKeyPress}
          />
          <button
            onClick={sendMessage}
            className="ml-2 px-6 py-3 rounded-lg bg-emerald-400/80 hover:bg-emerald-400 text-black font-semibold transition-all"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}
