import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import ChatMessage from "./components/ChatMessage";

const STORAGE_KEY = "aurora_chat_messages_v1";
const API_BASE = process.env.REACT_APP_BACKEND_URL || "http://127.0.0.1:8000";

export default function App() {
  const [messages, setMessages] = useState([]);
  const [draft, setDraft] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const listRef = useRef(null);

  // Load saved chat on mount
  useEffect(() => {
    try {
      const saved = JSON.parse(localStorage.getItem(STORAGE_KEY) || "[]");
      if (Array.isArray(saved)) setMessages(saved);
    } catch {}
  }, []);

  // Persist chat + auto-scroll on change
  useEffect(() => {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(messages));
    // scroll to bottom
    if (listRef.current) {
      listRef.current.scrollTo({ top: listRef.current.scrollHeight, behavior: "smooth" });
    }
  }, [messages]);

  const sendMessage = async () => {
  if (!message.trim()) return;

  setIsTyping(true);
  setResponse("");

  try {
    const res = await axios.post(`${API_BASE}/api/ask/`, { message });
    const reply = (res.data?.reply ?? "Sorry, I didn’t understand that.").toString();

    // Typing effect without duplication
    let currentText = "";
    for (let i = 0; i < reply.length; i++) {
      await new Promise((resolve) => setTimeout(resolve, 20)); // delay per char
      currentText += reply[i];
      setResponse(currentText);
    }

    setIsTyping(false);
  } catch (error) {
    console.error(error);
    setResponse("⚠️ Error connecting to server.");
    setIsTyping(false);
  }

  setMessage("");
};


  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="relative min-h-screen w-full text-[#e6eef6]">
      {/* Your portfolio background layers */}
      <div id="aurora-bg" />
      <div id="galaxy-glow" /> {/* from your style.css */}
      <div id="spotlight" />   {/* if you have it in style.css */}

      {/* Centered container */}
      <div className="relative z-10 mx-auto h-screen max-w-5xl px-4 md:px-6 flex flex-col">
        {/* Top logo */}
        <div className="flex items-center justify-center pt-5 pb-2">
          <img
            src="/logo.png" /* put your logo in /public */
            alt="Aurora Logo"
            className="h-12 w-auto rounded-full"
            style={{ boxShadow: "0 0 20px rgba(100,255,150,0.3)" }}
          />
        </div>

        {/* Chat area (scrollable) */}
        <div
          ref={listRef}
          className="chat-scroll flex-1 overflow-y-auto rounded-2xl border border-[rgba(255,255,255,0.06)] bg-[rgba(255,255,255,0.02)] shadow-aurora p-3 md:p-5"
          style={{ backdropFilter: "blur(6px)" }}
        >
          {messages.length === 0 ? (
            <div className="h-full flex items-center justify-center text-center text-[rgba(230,238,246,0.7)]">
              <div>
                <h2 className="text-xl md:text-2xl font-semibold text-aurora">Meet Aurora</h2>
                <p className="mt-2">Ask about Parsa, projects, or anything. I’ll reply here.</p>
              </div>
            </div>
          ) : (
            messages.map((m, i) => <ChatMessage key={i} role={m.role} content={m.content} />)
          )}

          {isTyping && (
            <div className="mt-2 text-[rgba(170,255,204,0.9)] italic">Aurora is typing<span className="animate-pulse">…</span></div>
          )}
        </div>

        {/* Input bar (fixed at bottom of container) */}
        <div className="mt-3 mb-5 rounded-2xl border border-[rgba(255,255,255,0.08)] bg-[rgba(20,25,25,0.9)] p-2 md:p-3 shadow-aurora"
             style={{ backdropFilter: "blur(6px)" }}>
          <div className="flex gap-2">
            <textarea
              value={draft}
              onChange={(e) => setDraft(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Type your message…"
              className="flex-1 resize-none h-[54px] md:h-[64px] rounded-xl bg-[#111414] border border-[#3a3a3a] px-3 py-3 focus:outline-none focus:border-aurora"
            />
            <button
              onClick={sendMessage}
              disabled={isTyping || !draft.trim()}
              className="min-w-[92px] rounded-xl bg-aurora text-[#0e1111] font-semibold px-4 hover:brightness-95 disabled:opacity-50"
            >
              Send
            </button>
          </div>
          <div className="mt-1 text-xs text-[rgba(255,255,255,0.45)]">Press Enter to send • Shift+Enter for newline</div>
        </div>
      </div>
    </div>
  );
}
